import ScreenHeading from "../../utilities/ScreenHeading/ScreenHeading";
import "./Portfolio.css";
import { Swiper, SwiperSlide } from "swiper/react"
import "swiper/css";
import Sidebar from "../../../src/images/sidebar.png";
import Ecommerce from '../../../src/images/ecommerce.png';
import HOC from "../../../src/images/hoc.png";
import MusicApp from "../../../src/images/musicapp.png";
import Book from "../../../src/images/portt.png";
import Taxi from "../../../src/images/taxi.png";
import Admin from "../../../src/images/admin.png";
import Animations from "../../utilities/Animations";
import ScrollService from "../../utilities/ScrollService";


export default function Portfolio(props)  {
  let fadeInScreenHandler = (screen) => {
    if (screen.fadeInScreen !== props.id) 
    return;
    Animations.animations.fadeInScreen(props.id);
  };
  const fadeInSubscription = ScrollService.currentScreenFadeIn.subscribe(fadeInScreenHandler);
  return (
    
    <div className="portfolio" id="portfolio">
    {/* heading */}
    <div className="main-container fade-in" id={props.id || ""}>
        <ScreenHeading
        subHeading = {"Recent Projects"}
        title={'PortfolioðŸ“·'}
     />
</div>

<div class="gallery">
  <a target="_blank" href="img_5terre.jpg">
    <img src="img_5terre.jpg" alt="Cinque Terre" width="600" height="400"></img>
  </a>
  <div class="desc">Add a description of the image here</div>
</div>

<div class="gallery">
  <a target="_blank" href="img_forest.jpg">
    <img src="img_forest.jpg" alt="Forest" width="600" height="400"></img>
  </a>
  <div class="desc">Add a description of the image here</div>
</div>

<div class="gallery">
  <a target="_blank" href="img_lights.jpg">
    <img src="img_lights.jpg" alt="Northern Lights" width="600" height="400"></img>
  </a>
  <div class="desc">Add a description of the image here</div>
</div>

<div class="gallery">
  <a target="_blank" href="img_mountains.jpg">
    <img src="img_mountains.jpg" alt="Mountains" width="600" height="400"></img>
  </a>
  <div class="desc">Add a description of the image here</div>
</div>
    </div>
    
  );
};
