import ScreenHeading from "../../utilities/ScreenHeading/ScreenHeading";
import "./Portfolio.css";
import Sidebar from "../../../src/images/sidebar.png";
import Ecommerce from '../../../src/images/ecommerce.png';
import HOC from "../../../src/images/hoc.png";
import MusicApp from "../../../src/images/musicapp.png";
import Book from "../../../src/images/portt.png";
import Taxi from "../../../src/images/taxi.png";
import Admin from "../../../src/images/admin.png";





import "swiper/css";

import Animations from "../../utilities/Animations";
import ScrollService from "../../utilities/ScrollService";


export default function Portfolio(props)  {
  let fadeInScreenHandler = (screen) => {
    if (screen.fadeInScreen !== props.id) 
    return;
    Animations.animations.fadeInScreen(props.id);
  };
  const fadeInSubscription = ScrollService.currentScreenFadeIn.subscribe(fadeInScreenHandler);
  return (
    
    <div className="portfolio" id="portfolio">
    {/* heading */}
    <div className="main-container fade-in" id={props.id || ""}>
        <ScreenHeading
        subHeading = {"Recent Projects"}
        title={'PortfoliosðŸ“·'}
     />
</div>


<div class="container">
	<div class="row">
		<div class="row">
            <div class="col-lg-3 col-md-4 col-xs-6 thumb">
                <a class="thumbnail" href="#" data-image-id="" data-toggle="" data-title="model"
                   data-image={Sidebar}
                   data-target="#">
                    <img class="img-thumbnail"
                         src={Sidebar}
                         alt="Another alt text"></img>
                </a>
            </div>
            <div class="col-lg-3 col-md-4 col-xs-6 thumb">
                <a class="thumbnail" href="#" data-image-id="" data-toggle="modal" data-title=""
                   data-image={Ecommerce}
                   data-target="#image-gallery">
                    <img class="img-thumbnail"
                         src={Ecommerce}
                         alt="Another alt text"></img>
                </a>
            </div>

            <div class="col-lg-3 col-md-4 col-xs-6 thumb">
                <a class="thumbnail" href="#" data-image-id="" data-toggle="modal" data-title=""
                   data-image={HOC}
                   data-target="#image-gallery">
                    <img class="img-thumbnail"
                         src={HOC}
                         alt="Another alt text"></img>
                </a>
            </div>
            <div class="col-lg-3 col-md-4 col-xs-6 thumb">
                <a class="thumbnail" href="#" data-image-id="" data-toggle="modal" data-title="Test1"
                   data-image={MusicApp}
                   data-target="#image-gallery">
                    <img class="img-thumbnail"
                         src={MusicApp}
                         alt="Another alt text"></img>
                </a>
            </div>
            <div class="col-lg-3 col-md-4 col-xs-6 thumb">
                <a class="thumbnail" href="#" data-image-id="" data-toggle="modal" data-title="Im so nice"
                   data-image={Book}
                   data-target="#image-gallery">
                    <img class="img-thumbnail"
                         src={Book}
                         alt="Another alt text"></img>
                </a>
            </div>



            <div class="col-lg-3 col-md-4 col-xs-6 thumb">
                <a class="thumbnail" href="#" data-image-id="" data-toggle="modal" data-title="Im so nice"
                   data-image={Taxi}
                   data-target="#image-gallery">
                    <img class="img-thumbnail"
                         src={Taxi}
                         alt="Another alt text"></img>
                </a>
            </div>
            <div class="col-lg-3 col-md-4 col-xs-6 thumb">
                <a class="thumbnail" href="#" data-image-id="" data-toggle="modal" data-title="Im so nice"
                   data-image={Admin}
                   data-target="#image-gallery">
                    <img class="img-thumbnail"
                         src={Admin}
                         alt="Another alt text"></img>
                </a>
            </div>
            <div class="col-lg-3 col-md-4 col-xs-6 thumb">
                <a class="thumbnail" href="#" data-image-id="" data-toggle="modal" data-title="Im so nice"
                   data-image={Ecommerce}
                   data-target="#image-gallery">
                    <img class="img-thumbnail"
                         src={Ecommerce}
                         alt="Another alt text"></img>
                </a>
            </div>
        </div>

        <div class="modal fade" id="image-gallery" tabindex="-1" role="dialog" aria-labelledby="myModalLabel" aria-hidden="true">
            <div class="modal-dialog modal-lg">
                <div class="modal-content">
                    <div class="modal-header">
                        <h4 class="modal-title" id="image-gallery-title"></h4>
                        <button type="button" class="close" data-dismiss="modal"><span aria-hidden="true">Ã—</span><span class="sr-only">Close</span>
                        </button>
                    </div>
                    <div class="modal-body">
                        <img id="image-gallery-image" class="img-responsive col-md-12" src=""></img>
                    </div>
                    <div class="modal-footer">
                        <button type="button" class="btn btn-secondary float-left" id="show-previous-image"><i class="fa fa-arrow-left"></i>
                        </button>

                        <button type="button" id="show-next-image" class="btn btn-secondary float-right"><i class="fa fa-arrow-right"></i>
                        </button>
                    </div>



                    
                </div>
            </div>
        </div>

        
        
	</div>
</div>





    </div>
    
  );
};
